import java.io.*;
import java.util.*;

public class timeloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner in = new Scanner(System.in);
		int x = in.nextInt();
		for (int i = 1; i <= x; i++) {
			System.out.println(i + " Abracadabra");
		}
	}

}
